let money,
name,
time,
price;

function start() {
	money = prompt("Ваш бюджет?", "");

	while	(isNaN(money) || money == "" || money == null) {
		money = prompt("Ваш бюджет?", "");
	}
	name	= prompt("Название вашего магазина?", "").toUpperCase();
	time = prompt("Введите время", "");
}
start();

function budgetOneDay(money) {
	budget = money / 30;
}
budgetOneDay(money);

let mainList = {
	money: money,
	budgetOneDay: budget,
	shopName: name,
	shopGoods: [],
	timeWork: time,
	employers: {},
	status: false,
	discount: false
};

function chooseGoods() {
	for (let i = 0; i < 5; i++) {
		let a = prompt("Какой тип товаров будем продавать?", "");
		if ((typeof(a)) === 'string' && (typeof(a)) !== 'null' && a != '' && a.length < 50){
			console.log('верный тип товара');
			mainList.shopGoods[i] = a;
		} else {
			i = i - 1;
		}
	}
}
chooseGoods();

function employersName() {
	for (let i = 0; i < 4; i++) {
		let a = prompt("Введите имя нового сотрудника", "");
		if ((typeof(a)) === 'string' && (typeof(a)) !== 'null' && a != '' && a.length < 20){
			console.log('правильное имя');
			mainList.employers['associate' + i] =  a;
		} else {
			i = i - 1;
		}
	}
}
employersName();

function workTime(time) {	
	while	(isNaN(time) || time == "" || time == null) {
		time = prompt("Введите время", "");
	}
	if (time < 0) {
		console.log('такого не может быть');
	} else if ( time > 8 && time < 20) {
		console.log('время работать');
	} else if (time < 24) {
		console.log('уже поздно');
	} else {
		console.log('пора спать');
	}
}
workTime(time);

function score(price) {
	price = prompt("Введите цену на товар", "");
	while	(isNaN(price) || price == "" || price == null) {
		price = prompt("Введите цену на товар", "");
	}
	if (mainList.discount) {
		price = price * 0.8;
		console.log(price);
	} else {
		console.log('скидка не предоставляется');
	}
}
score(price);



