let money,
name,
time,
minBudget,
price;

function start() {
	money = prompt('Ваш бюджет?', '');
	while	(isNaN(money) || money == '' || money == null) {
		money = prompt('Ваш бюджет?', '');
	}
	name	= prompt('Название вашего магазина?', '').toUpperCase();
	time = prompt('Введите время', '');
}
start();

let mainList = {
	budget: money,
	budgetOneDay: minBudget,
	shopName: name,
	shopGoods: [],
	timeWork: time,
	employers: {},
	open: false,
	discount: false,
	shopItems: [],
	chooseGoods: function chooseGoods() {
		for (let i = 0; i < 5; i++) {
			let a = prompt('Какой тип товаров будем продавать?', '');
			if ((typeof(a)) === 'string' && (typeof(a)) !== 'null' && a != '' && a.length < 50){
				console.log('верный тип товара');
				mainList.shopGoods[i] = a;
			} else {
				i--;
			}
		}
	},
	workTime: function workTime(time) {	
		while	(isNaN(time) || time == '' || time == null) {
			time = prompt('Введите время', '');
		}
		if (time < 0) {
			console.log('такого не может быть');
		} else if ( time > 8 && time < 20) {
			console.log('время работать');
			mainList.open = true;
		} else if (time < 24) {
			console.log('уже поздно');
		} else {
			console.log('пора спать');
		}
	},
	budgetOneDay: function budgetOneDay(money) {
		minBudget = money / 30;
	},
	score: function score(price) {
		price = prompt('Введите цену на товар', '');
		while	(isNaN(price) || price == '' || price == null) {
			price = prompt('Введите цену на товар', '');
		}
		if (mainList.discount) {
			price = price * 0.8;
			console.log(price);
		} else {
			console.log('скидка не предоставляется');
		}
	},
	employersName: function employersName() {
		for (let i = 0; i < 4; i++) {
			let a = prompt('Введите имя нового сотрудника', '');
			if ((typeof(a)) === 'string' && (typeof(a)) !== 'null' && a != '' && a.length < 20){
				console.log('правильное имя');
				mainList.employers['associate' + i] =  a;
			} else {
				i--;
			}
		}
	},
	chooseShopItems: function chooseShopItems() {		
		for (let i = 0; i < 1; i++) {

			let items = prompt('Перечислите через запятую ваши товары', '');
			if ((typeof(items)) === 'string' && items !== 'null' && items !== '' && items.length < 255){
				mainList.shopItems = items.split(',');
			}else{
				i--;
				console.log('неверный ввод данных.');
			}
		}
		for (let i = 0; i < 1; i++) {
			let itemsAdd = prompt('Предложите, ещё...', '');
			if ((typeof(itemsAdd)) === 'string' && itemsAdd !== 'null' && itemsAdd !== '' && itemsAdd.length < 255){
				mainList.shopItems.push(itemsAdd);
			}else{
				i--;
				console.log('неверный ввод данных.');
			}
		}
		mainList.shopItems.sort();		
		mainList.shopItems.forEach(function(item, i, shopItems) {
			console.log('У нас вы можете купить: ' + item );
		});
	}
};
